# Complete the minimumAbsoluteDifference function below.
def minimumAbsoluteDifference(arr):
    arr.sort()
    min = abs(arr[1] - arr[0])
    for i in range(2,len(arr)):
        temp = abs(arr[i] - arr[i-1])
        if min > temp:
            min = temp
    return min



